drop table `analytics`.`auditor_rates`;
CREATE TABLE `analytics`.`auditor_rates` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `country` varchar(45) NOT NULL,
  `currency_iso_code` varchar(3) NOT NULL,
  `business_line` enum('Management Systems','Agri-Food','Product Services', 'All') NOT NULL,
  `resource_type` enum('Employee','Contractor') NOT NULL,
  `period` date NOT NULL,
  `value` decimal(16,6) NOT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;



select count(*) from `analytics`.`auditor_rates`;

select ar.country, ar.business_line, ar.resource_type,
avg(ar.value/ct.ConversionRate) as 'Avg Hourly Rate (AUD)'
from `analytics`.`auditor_rates` ar
left join salesforce.currencytype ct on ar.currency_iso_code = ct.IsoCode
group by ar.country, ar.business_line, ar.resource_type;
