# Assurance
#explain
(select
t.*,
# t.Id as 'Client Id', t.Name as 'Client Name', t.Client_Ownership__c as 'Compass Client Ownership', t.Country, t.State, t.`Industry (Compass)`, t.`Industry (Guess)`, 
sum(inv.Total_Amount__c) as '12 Months Revenue', group_concat(distinct inv.CurrencyIsoCode) as 'Currency',
ili.Product_Category__c as 'Invoice Category',
year(inv.Invoice_Processed_Date__c) as 'Year' ,
sum(ili.Total_Line_Amount__c) 
#min(inv.Invoice_Processed_Date__c) as 'Last Invoiced', 
from 
(select a.Id, a.Name, a.Client_Ownership__c, 
ccs.Name as 'Country', scs.Name as 'State', 
if(i.Name in ('03 - 02 | Manufacture of beverages','03 - 05 | Manufacture of food products'),'03 - Manufacturing - Food & Beverages',i.Industry_Sector__c) as 'Industry (Compass)',
if(i.Name = '99 | To be Confirmed',
	if( group_concat(distinct if(scspc.IsDeleted = 0 and code.IsDeleted = 0 and scsp.IsDeleted = 0 and sc.IsDeleted = 0 and c.IsDeleted = 0 and code.Name like 'NACE%', salesforce.getIndustryFromNace(substring_index(replace(code.Name,'NACE: ',''),'.',1)), null)) is null,
		if(group_concat(distinct prog.Business_Line__c) like '%Product Services%', 
			'03 - Manufacturing',
            if(group_concat(distinct prog.Business_Line__c) like '%Food%', 
				'03 - Manufacturing - Food & Beverages',
                if(group_concat(distinct std.Name) like '%Disability%' or group_concat(distinct std.Name) like '%DSS%', 
					'13 - Human Health and Social Work', 
                    if(group_concat(distinct if(scspc.IsDeleted = 0 and code.IsDeleted = 0 and scsp.IsDeleted = 0 and sc.IsDeleted = 0 and c.IsDeleted = 0 and code.Name like 'SAI%', code.Name, null)) like '%SE01%' or 
						group_concat(distinct if(scspc.IsDeleted = 0 and code.IsDeleted = 0 and scsp.IsDeleted = 0 and sc.IsDeleted = 0 and c.IsDeleted = 0 and code.Name like 'SAI%', code.Name, null)) like '%SH01%',
						'02 - Mining and Quarrying',
                        if(a.Client_Ownership__c='Product Services','03 - Manufacturing','99 | To be Confirmed'))
                    )
				)
            ),
        #group_concat(distinct if(scspc.IsDeleted = 0 and code.IsDeleted = 0 and scsp.IsDeleted = 0 and sc.IsDeleted = 0 and c.IsDeleted = 0 and code.Name like 'NACE%', getIndustryFromNace(substring_index(replace(code.Name,'NACE: ',''),'.',1)), null))),
        min(if(scspc.IsDeleted = 0 and code.IsDeleted = 0 and scsp.IsDeleted = 0 and sc.IsDeleted = 0 and c.IsDeleted = 0 and code.Name like 'NACE%', salesforce.getIndustryFromNace(substring_index(replace(code.Name,'NACE: ',''),'.',1)), '99 | To be Confirmed'))),
	if(i.Name in ('03 - 02 | Manufacture of beverages','03 - 05 | Manufacture of food products'),'03 - Manufacturing - Food & Beverages',i.Industry_Sector__c) 
) as 'Industry (Guess)', 
group_concat(distinct if(scspc.IsDeleted = 0 and code.IsDeleted = 0 and scsp.IsDeleted = 0 and sc.IsDeleted = 0 and c.IsDeleted = 0, code.Name, null)) as 'Codes',
group_concat(distinct std.Name) as 'Primary Standards',
group_concat(distinct prog.Business_Line__c) as 'Business Lines',
group_concat(distinct if(scspc.IsDeleted = 0 and code.IsDeleted = 0 and scsp.IsDeleted = 0 and sc.IsDeleted = 0 and c.IsDeleted = 0 and code.Name like 'NACE%', code.Name, null)) as 'Nace Codes',
group_concat(distinct if(scspc.IsDeleted = 0 and code.IsDeleted = 0 and scsp.IsDeleted = 0 and sc.IsDeleted = 0 and c.IsDeleted = 0 and code.Name like 'NACE%', substring_index(replace(code.Name,'NACE: ',''),'.',1), null)) as 'Nace Sectors',
group_concat(distinct if(scspc.IsDeleted = 0 and code.IsDeleted = 0 and scsp.IsDeleted = 0 and sc.IsDeleted = 0 and c.IsDeleted = 0 and code.Name like 'NACE%', salesforce.getIndustryFromNace(substring_index(replace(code.Name,'NACE: ',''),'.',1)), null)) as 'Industries',
group_concat(distinct if(scspc.IsDeleted = 0 and code.IsDeleted = 0 and scsp.IsDeleted = 0 and sc.IsDeleted = 0 and c.IsDeleted = 0 and code.Name like 'SAI%', code.Name, null)) as 'SAI Codes'
from salesforce.account a
inner join salesforce.account inv_site on inv_site.ParentId = a.Id
inner join salesforce.industry__c i on a.Industry_2__c = i.Id
left join salesforce.country_code_setup__c ccs on inv_site.Business_Country2__c = ccs.Id
left join salesforce.state_code_setup__c scs on inv_site.Business_State__c = scs.Id
left join salesforce.certification__c c on c.Primary_client__c = a.Id
left join salesforce.standard_program__c sp on c.Primary_Standard__c = sp.Id
left join salesforce.standard__c std on sp.Standard__c = std.Id
left join salesforce.program__c prog on sp.Program__c = prog.Id
left join salesforce.certification__c sc on sc.Primary_Certification__c = c.Id
left join salesforce.site_certification_standard_program__c scsp on scsp.Site_Certification__c = sc.Id
left join salesforce.site_cert_standard_program_code__c scspc on scspc.Site_Certification_Standard_Program__c = scsp.Id
left join salesforce.code__c code on scspc.Code__c = code.Id
where a.Record_Type_Name__c = 'Client'
and a.IsDeleted = 0
and a.Status__c = 'Active'
and a.Client_Ownership__c in ('Australia', 'Product Services')
and if(a.Client_Ownership__c in ('Australia', 'Product Services'), inv_site.Finance_Statement_Site__c, 1)
group by a.Id) t
inner join salesforce.invoice__c inv on inv.Billing_Client__c = t.Id
inner join salesforce.invoice_line_item__c ili on ili.Invoice__c = inv.Id
where 
date_format(inv.Invoice_Processed_Date__c, '%Y-%m') >= '2012-07'
and date_format(inv.Invoice_Processed_Date__c, '%Y-%m') < '2017-07' 
and inv.isDeleted=0 
and ili.IsDeleted = 0 
and inv.Status__c in ('Open', 'Closed')
group by t.Id, `Year`);

# Assurance TIS
#explain
(select 
'Assurance' as 'Division',
a.Id as 'Client Id',
a.Name as 'Client Name',
'' as 'Compass Client Ownership',
a.BillingCountry,
a.BillingCountry as 'Country (Equivalent)',
a.BillingState,
if(r.Class_Type__c='Public Class', if(r.Course_Type__c='eLearning', 'TIS - eLearning', 'TIS - Public'), 'TIS - In House') as 'Type',
a.Industry as 'Industry (Salesforce)',
'' as 'Industry (Guess)',
'' as 'Industry (Assurance Equivalent)',
sum(if(i.GST_Exempt__c, i.PSoft_Inv_Amt__c, i.PSoft_Inv_Amt__c/1.1)) as 'Revenues',
i.CurrencyIsoCode
from training.invoice_ent__c i
inner join training.registration__c r on i.Registration__c = r.Id
inner join training.account a on r.Billing_Account__c = a.Id
inner join training.recordtype rt on a.RecordTypeId = rt.Id
where
r.IsDeleted = 0
and i.IsDeleted = 0
and i.CreatedDate >= '2014-06-01'
and i.CreatedDate <= '2015-05-31'
and rt.Name in ('ENT - APAC - Account', 'ENT - APAC - Parent Account')
group by a.Id);

