DROP TABLE IF EXISTS `auditors`;
CREATE TABLE `auditors` (
	`id` int(11) NOT NULL AUTO_INCREMENT,
	`name`	varchar(128) NOT NULL COMMENT 'Auditor Full Name', 
    `home_address` varchar(256) NOT NULL COMMENT 'Auditor Home Address',
    `country` varchar(64) NOT NULL,
    `type` varchar(64),
    `level` varchar(64) COMMENT 'LA/Auditor',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 ROW_FORMAT=COMPACT;


